INSERT INTO `{{diary}}` (`diaryid`, `uid`, `diarytime`, `nextdiarytime`, `addtime`, `content`, `attachmentid`, `shareuid`, `readeruid`, `remark`, `stamp`, `isreview`, `attention`, `commentcount`) VALUES (1, 2, {date+1}, {date}, {time}, '<p><span style=\"color:#58585c;line-height:21px;font-family:宋体;font-size:14px;background-color:#ffffff;\">“被业界称为用户体验最好的协同管理平台-IBOS”独具匠心的将类社区中的积分体系及微博、微信朋友圈@功能导入管理软件中，有效地引导员工积极参与内部协同办公平台的使用和促进员工互动。下面，我们就具体分析一下，如何基于IBOS的将类社区中的积分体系及微博、微信朋友圈@功能让企业管理平台活跃起来</span></p>', '', '', '', '', 0, 0, '', 0);
INSERT INTO `{{diary}}` (`diaryid`, `uid`, `diarytime`, `nextdiarytime`, `addtime`, `content`, `attachmentid`, `shareuid`, `readeruid`, `remark`, `stamp`, `isreview`, `attention`, `commentcount`) VALUES (2, 3, {date}, {date+1}, {time}, '<p>1、区域市场日常沟通及市场项目支持</p><p>2、区域市场活动策划与推广配合展会、会议营销、合作会议等</p><p>&nbsp;</p>', '', '', '2', '', 1, 1, '', 0);
